import { useDropzone } from "react-dropzone";
import axios from "axios";
import leaseStyles from "../../styles/upload-lease/lease.module.css";

const lease = (props) => {
  const { acceptedFiles, getRootProps, getInputProps } = useDropzone();

  const files = acceptedFiles.map((file) => (
    <li key={file.path}>
      {file.path} - {file.size} bytes
    </li>
  ));

  const uploadFiles = async () => {
    for (let i = 0; i < acceptedFiles.length; i++) {
      const data = new FormData();
      const file = acceptedFiles[i];
      data.append("file", file);
      const options = {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      };
      console.log(data);
      //   const { status } = await axios
      //     .create({ headers: formData.getHeaders() })
      //     .post("/api/upload-lease", formData);
      //   console.log({ status });
    }
  };

  return (
    <div className={leaseStyles.container}>
      <div {...getRootProps({ className: `${leaseStyles.dropzone}` })}>
        <input {...getInputProps()} />
        <p className={leaseStyles.text}>
          Drag 'n' drop some files here, or click to select files
        </p>
      </div>
      <aside>
        <h2>Files</h2>
        <ul>{files}</ul>
      </aside>
      <button onClick={uploadFiles}>Submit</button>
    </div>
  );
};

export default lease;
