import { useDropzone } from "react-dropzone";
import axios from "axios";

import "../../styles/upload-lease/lease.module.css";

const lease = (props) => {
  const { acceptedFiles, getRootProps, getInputProps } = useDropzone();

  const files = acceptedFiles.map((file) => (
    <li key={file.path}>
      {file.path} - {file.size} bytes
    </li>
  ));

  const uploadFiles = async () => {
    const signedUrl =
      "https://wellick-lease-image-s3-stage.s3.amazonaws.com/lease-image-1-1600801947234?AWSAccessKeyId=AKIAWQRXSB72EUDRGGZB&Expires=1600804947&Signature=td91TsTLE3LKoEf6%2FwHbVj78J4w%3D";

    for (let i = 0; i < acceptedFiles.length; i++) {
      const file = acceptedFiles[i];
      const options = {
        headers: {
          "Content-Type": file.type,
        },
      };
      axios.put(signedUrl, file, options);
    }
  };

  return (
    <div className="container">
      <div {...getRootProps({ className: "dropzone" })}>
        <input {...getInputProps()} />
        <p>Drag 'n' drop some files here, or click to select files</p>
      </div>
      <aside>
        <h4>Files</h4>
        <ul>{files}</ul>
      </aside>
      <button onClick={uploadFiles}>Submit</button>
    </div>
  );
};

export default lease;
